# generated by stubbs:add-option
# Thu 28 Jun 2012 00:58:51 EDT

# print USAGE and exit
rerun_option_error() {
    [ -z "$USAGE"  ] && echo "$USAGE" >&2
    [ -z "$SYNTAX" ] && echo "$SYNTAX $*" >&2
    return 2
}

# check option has its argument
rerun_option_check() {
    [ "$1" -lt 2 ] && rerun_option_error
}

# options: [repo]
while [ "$#" -gt 0 ]; do
    OPT="$1"
    case "$OPT" in
          -r|--repo) rerun_option_check $# ; REPO=$2 ; shift ;;
        # unknown option
        -?)
            rerun_option_error
            ;;
        # end of options, just arguments left
        *)
          break
    esac
    shift
done

# If defaultable options variables are unset, set them to their DEFAULT
# Check required options are set
[ -z "$REPO" ] && { echo "missing required option: --repo" ; return 2 ; }
#
return 0
